import React, { useState } from 'react';
import "../fileuploader.css";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

export default function FileUploader() {
    var [passengers, setpassengers] = useState(["",0]);
    var [words, setwords] = useState([""]);

    function handleFile(e) {
        e.preventDefault()
        try{
            const reader = new FileReader()
            reader.onload = async (e) => { 
              const text = (e.target.result)
    
              console.log(text)
    
            };
            reader.readAsText(e.target.files[0])
            console.log(e.target.files[0]);    
            toast.success("Uploaded successfully");

        }
        catch (error) {
            toast.error("Issue happened while uploading");

            console.error(error);
          }
   
    }

    return (
        <div className="form-control">
            <ToastContainer />
            <div className="form-row">
                <div className="form-group col-md-6">
                    <label className="text-white">Select File :</label>
                    <input type="file" className="form-control" name="upload_file" onChange={handleFile} />
                </div>
            </div>
            <br />
            <div>
                <h2>Words</h2>
                <table className="table table-bordered">
                    <thead>
                        <tr>
                            <td scope="col"> Name </td>
                            <td scope="col"> Nationality </td>
                        </tr>
                    </thead>
                    <tbody>
                        {passengers.map(item =>

                            <tr key={item.passport} scope="row">
                                <td>{item.name}</td>
                                <td>{item.nationality}</td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
        </div >
    )
}
