import axios from "axios";
import React, { useState } from 'react';
import "../fileuploader.css";

export default function FileUploader() {
    var [passengers, setpassengers] = useState([]);


    function handleFile(e) {

        const data = new FormData();
        data.append('file', e.target.files[0]);

        axios.post("https://localhost:7297/api/claimTransactions/Attach", data)
            .then(res => { 
                setpassengers(res.data);
                console.log(passengers);
            })
            .catch(error => {
                console.log(error);

            })
    }

    return (
        <div className="form-control">
            <div className="form-row">
                <div className="form-group col-md-6">
                    <label className="text-white">Select File :</label>
                    <input type="file" className="form-control" name="upload_file" onChange={handleFile} />
                </div>
            </div>
            <br />
            <div>
                <h2>Words</h2>
                <table className="table table-bordered">
                    <thead>
                        <tr>
                            <td scope="col"> Name </td>
                            <td scope="col"> Nationality </td>
                        </tr>
                    </thead>
                    <tbody>
                        {passengers.map(item =>

                            <tr key={item.passport} scope="row">
                                <td>{item.name}</td>
                                <td>{item.nationality}</td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
        </div >
    )
}
