import React, { useState } from "react";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "bootstrap/dist/css/bootstrap.css";

export default function FileUploader() {
  var [words, setwords] = useState([{ name: "", num: 0 }]);
  var [wordsToDispkay, setwordsToDispkay] = useState([""]);

  function handleFile(e) {
    e.preventDefault();

    if (e.target.files[0].type === "text/plain") {
      try {
        const reader = new FileReader();
        reader.onload = async (e) => {
          const text = e.target.result;

          console.log("test" + text);
          var texts = text.split(" ");
          setwordsToDispkay(texts);
          //console.log("testsss" + texts);
          //for(var i=0; i< texts.length; i++)
          //{
          //var w = texts[i];
          //var newwords = [...words];
          //debugger;

          //newwords.push({w: 1});
          // setwords(newwords);
          // console.log("words" + words);

          //}
        };
        reader.readAsText(e.target.files[0]);
        console.log(e.target.files[0]);
        toast.success("Uploaded successfully");
      } catch (error) {
        toast.error("Issue happened while uploading");

        console.error(error);
      }
    } else {
      toast.error("File type not supported");
    }
  }

  return (
    <div className="form-control">
      <ToastContainer />
      <div className="form-row">
        <div className="form-group col-md-6">
          <label className="text-white">Select File :</label>
          <input
            type="file"
            className="form-control"
            name="upload_file"
            onChange={handleFile}
          />
        </div>
      </div>
      <br />
      <div>
        <h2>Words</h2>
        <table className="table table-bordered">
          <thead>
            <tr>
              <td scope="col"> Word </td>
              <td scope="col"> Number Of Occurence </td>
            </tr>
          </thead>
          <tbody>
            {wordsToDispkay.map((item) => (
              <tr scope="row">
                <td>{item}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
