import axios from "axios";
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Passenger from "./PassengerDetails";

export default function FileUploader() {
    var [passengers, setpassengers] = useState([]);
    const navigate = useNavigate();

    function handleNavigation(item) {

        <Passenger passenger={item} />
    }

    function handleFile(e) {

        const data = new FormData();
        data.append('file', e.target.files[0]);

        axios.post("https://localhost:7297/api/claimTransactions/Attach", data)
            .then(res => { 
                setpassengers(res.data);
                console.log(passengers);
            })
            .catch(error => {
                console.log(error);

            })
    }

    return (
        <div>
            <div className="form-row">
                <div className="form-group col-md-6">
                    <label className="text-white">Select File :</label>
                    <input type="file" className="form-control" name="upload_file" onChange={handleFile} />
                </div>
            </div>
            <br />
            <div>
                <h2>Passengers</h2>
                <table className="table table-bordered">
                    <thead>
                        <tr>
                            <td scope="col"> Name </td>
                            <td scope="col"> Nationality </td>
                            <td scope="col"> Passport </td>
                            <td scope="col"> Age </td>
                            <td scope="col"> Num Of Flights </td>
                            <td scope="col"> Actions </td>
                        </tr>
                    </thead>
                    <tbody>
                        {passengers.map(item =>

                            <tr key={item.passport} scope="row">
                                <td>{item.name}</td>
                                <td>{item.nationality}</td>
                                <td>{item.passport}</td>
                                <td>{item.age}</td>
                                <td>{item.flights.length}</td>
                                <button
                                    type="button" class="btn btn-info"
                                    onClick={() => handleNavigation(item)}>
                                    Details
                                </button>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
        </div >
    )
}
